import{Controller}from"@hotwired/stimulus";var celebration_modal,save_answer_modal;export default class extends Controller{static targets=["name","output","points","message"];connect(){import("bootstrap").then((e=>{celebration_modal=new e.Modal(document.getElementById("celebrationModal"),{keyboard:!1}),save_answer_modal=new e.Modal(document.getElementById("saveAnswerModal"),{keyboard:!1})}))}celebrate({detail:{points:e}}){this.pointsTarget.innerHTML=e,celebration_modal.show(),setTimeout((function(){celebration_modal.hide()}),2500)}save_answer({detail:{message:e}}){null!=e&&"undefined"!==e||(e="Deine Antwort wurde gespeichert!"),this.messageTarget.innerHTML=e,save_answer_modal.show(),setTimeout((function(){save_answer_modal.hide()}),2500)}}
