import{Controller}from"@hotwired/stimulus";export default class extends Controller{static targets=["name","output"];greet(){this.outputTarget.textContent=`Hello, ${this.nameTarget.value}!`}finish_exercise({detail:{points:t}}){console.log("finish_exercise");const e=new CustomEvent("correct-answer",{detail:{points:t}});window.dispatchEvent(e);const o=new CustomEvent("celebrate",{detail:{points:t}});window.dispatchEvent(o)}}