import{Controller}from"@hotwired/stimulus";export default class extends Controller{static targets=["name","output"];greet(){this.outputTarget.textContent=`Hello, ${this.nameTarget.value}!`}save_answer({detail:{message:e}}){console.log("save_answer");const t=new CustomEvent("save-answer",{detail:{message:e}});window.dispatchEvent(t)}finish_exercise({detail:{points:e}}){const t=new CustomEvent("correct-answer",{detail:{points:e}});window.dispatchEvent(t);const s=new CustomEvent("celebrate",{detail:{points:e}});window.dispatchEvent(s)}}
