import{Controller}from"@hotwired/stimulus";import*as Ajax from"ajax";var myPoints=3,pointsDeductionForMistake=1,solution=[];export default class extends Controller{static targets=["button","name","output","accountId","exerciseId","exerciseClass","totalPossiblePoints","pointsDeductionForMistake","input","solution"];connect(){import("jquery_with_setup").then((t=>{myPoints=this.totalPossiblePointsTarget.getAttribute("data-value"),pointsDeductionForMistake=this.pointsDeductionForMistakeTarget.getAttribute("data-value"),solution=this.solutionTarget.getAttribute("data-value")}))}disconnect(){}check(){solution==this.inputTarget.value?(new Ajax.ajax).updateExerciseHistoryOfAccount(this.accountIdTarget.getAttribute("data-value"),this.exerciseClassTarget.getAttribute("data-value"),this.exerciseIdTarget.getAttribute("data-value"),myPoints).then((()=>{const t=new CustomEvent("correct-answer",{detail:{points:myPoints}});window.dispatchEvent(t)})):myPoints-pointsDeductionForMistake<0?myPoints=0:myPoints-=pointsDeductionForMistake}}
