import{Controller}from"@hotwired/stimulus";import*as Ajax from"ajax";export default class extends Controller{static targets=["user_id","accountId","courseId","output"];updateCounter({detail:{points:t}}){(new Ajax.ajax).getPointsFromCourse(this.accountIdTarget.getAttribute("data-value"),this.courseIdTarget.getAttribute("data-value")).then((t=>{console.log("getPointsFromCourse"),console.log(t),this.incrementallyUpdate(this.outputTarget,t)}))}incrementallyUpdate(t,e){console.log("incrementallyUpdate"),console.log(this.outputTarget.innerHTML);var o=parseInt(this.outputTarget.innerHTML),r=e;console.log("current value: "+o),console.log("target value: "+r),console.log(this.outputTargets);for(;o!=r;)if(console.log("current value: "+o),console.log("target value: "+r),o<r){o=parseInt(this.outputTarget.innerHTML)+1;for(var a=0;a<this.outputTargets.length;a++)this.outputTargets[a].innerHTML=o}else{o=parseInt(this.outputTarget.innerHTML)-1;for(a=0;a<this.outputTargets.length;a++)this.outputTargets[a].innerHTML=o}}}