import{Controller}from"@hotwired/stimulus";import*as Ajax from"ajax";export default class extends Controller{static targets=["user_id","accountId","courseId","output"];updateCounter({detail:{points:t}}){(new Ajax.ajax).getPointsFromCourse(this.accountIdTarget.getAttribute("data-value"),this.courseIdTarget.getAttribute("data-value")).then((t=>{this.incrementallyUpdate(this.outputTarget,t)}))}incrementallyUpdate(t,e){for(var r=parseInt(this.outputTarget.innerHTML),a=e;r!=a;)if(r<a){r=parseInt(this.outputTarget.innerHTML)+1;for(var s=0;s<this.outputTargets.length;s++)this.outputTargets[s].innerHTML=r}else{r=parseInt(this.outputTarget.innerHTML)-1;for(s=0;s<this.outputTargets.length;s++)this.outputTargets[s].innerHTML=r}}}